package com.lzhpo.crypto.test.mock;

import cn.hutool.core.codec.Base64;
import cn.hutool.core.util.RandomUtil;
import cn.hutool.crypto.SecureUtil;
import java.security.KeyPair;
import org.junit.jupiter.api.Test;

/**
 * @author lzhpo
 */
class TestUtils {

    /**
     * privateKey: 
     * publicKey: MIGfMA0GCSqGSIb3DQEBAQUAA4GNADCBiQKBgQDcoSP/XuXr0Ms0S1bFJm0HrHUZe317vWQJepWalci/sqX+hZkKLh/PTkxL/yY5sm7gIWvc7GMDQCkqG0kQE2qmEgr3cKD0YFBURX6unnetZKTufg/k/197/OSZlyJ4JIngnvZ+W+Sk91zbRxQV1bGd/6J1hGGUYRTE7DLCpL4owQIDAQAB
     */
    @Test
    void generateRsaKey() {
        KeyPair keyPair = SecureUtil.generateKeyPair("RSA");

        String privateKey = Base64.encode(keyPair.getPrivate().getEncoded());
        System.out.println("RSA privateKey: " + privateKey);

        String publicKey = Base64.encode(keyPair.getPublic().getEncoded());
        System.out.println("RSA publicKey: " + publicKey);
    }

    @Test
    void generateSm4Key() {
        // SM4 requires a 128 bit key
        // 1 byte = 1 bit
        // 16 bit = 128 bit
        // so the SM4 min key length is 16
        String key = RandomUtil.randomString(16);
        System.out.println("SM4 key: " + key);
    }
}
